$(document).ready(function(){var t=-15,i=1.2;$(".item").hover(function(){width=$(".item").width()*i,height=$(".item").height()*i,$(this).find("img").stop(!1,!0).animate({width:width,height:height,top:t,left:t},{duration:200}),$(this).find("div.caption").stop(!1,!0).fadeIn(200)},function(){$(this).find("img").stop(!1,!0).animate({width:$(".item").width(),height:$(".item").height(),top:"0",left:"0"},{duration:100}),$(this).find("div.caption").stop(!1,!0).fadeOut(200)})});